mapper.insert:
INSERT INTO A_CODE_GROUP (
    CODE_GROUP_ID,
    USE_YN
) VALUES (
    #{codeGroupId},
    #{useYn}
)

mapper.update:
UPDATE A_CODE_GROUP
SET
    USE_YN = #{useYn}
WHERE
    CODE_GROUP_ID = #{codeGroupId}

mapper.insert:
INSERT INTO A_CODE_GROUP_LANG (
    CODE_GROUP_ID, 
    LANG_CD, 
    CODE_GROUP_NM
) VALUES (
    #{codeGroupId}, 
    #{langCd}, 
    #{codeGroupNm}
)
ON DUPLICATE KEY
UPDATE 
    CODE_GROUP_NM = #{codeGroupNm}

mapper.delete:
DELETE FROM A_CODE_GROUP_LANG
WHERE
    CODE_GROUP_ID = #{codeGroupId}
    AND LANG_CD = #{langCd}

mapper.update:
UPDATE A_CODE
SET
    OPTION1 = #{option1},
    OPTION2 = #{option2},
    OPTION3 = #{option3},
    SEQ = #{seq}
WHERE
    CODE_ID = #{codeId}
    AND CODE_GROUP_ID = #{codeGroupId}

mapper.delete:
DELETE FROM A_CODE
WHERE
    CODE_ID = #{codeId}
    AND CODE_GROUP_ID = #{codeGroupId}

DELETE FROM A_CODE_LANG
WHERE
    CODE_ID = #{codeId}
    AND CODE_GROUP_ID = #{codeGroupId}
    AND LANG_CD = #{langCd}
